import React from "react";
import { Link } from "react-router-dom";
import { useForm, SubmitHandler } from "react-hook-form";
import Header from "../../components/common/Header/Header";
import homeImage from "../../assets/images/home.png";

interface LoginFormInputs {
  email: string;
  password: string;
}

const Login: React.FC = () => {
  const {
    register,
    handleSubmit,
    formState: { errors }
  } = useForm<LoginFormInputs>();

  const onSubmit: SubmitHandler<LoginFormInputs> = (data) => {
    console.log(data);
    // Add your login logic here
  };

  return (
      <div className="h-screen flex">
        {/* Left Column - Image */}
        <div className="w-1/2 relative">
          <img
              src={homeImage}
              alt="Logistics Services"
              className="w-full h-full object-cover"
          />
        </div>

        {/* Right Column - Header and Form */}
        <div className="w-1/2 flex flex-col">
          {/* Header at the top */}
          <Header />

          {/* Content area with form */}
          <div className="flex-grow flex flex-col items-center justify-center p-8">
            <div className="space-y-6 w-full max-w-xs">
              <h2 className="text-3xl font-bold mb-12">Iniciar sesión</h2>

              <form className="space-y-4" onSubmit={handleSubmit(onSubmit)}>
                <div className="space-y-2">
                  <label htmlFor="email" className="block text-sm font-bold">
                    Correo
                  </label>
                  <input
                      id="email"
                      {...register("email", {
                        required: "Email es requerido",
                        pattern: {
                          value: /^[A-Z0-9._%+-]+@[A-Z0-9.-]+\.[A-Z]{2,}$/i,
                          message: "Email inválido"
                        }
                      })}
                      className={`w-full px-3 py-2 border ${errors.email ? 'border-red-500' : 'border-gray-300'} rounded-3xl`}
                  />
                  {errors.email && <p className="text-red-500 text-xs">{errors.email.message}</p>}
                </div>

                <div className="space-y-2">
                  <label htmlFor="password" className="block text-sm font-bold">
                    Contraseña
                  </label>
                  <input
                      id="password"
                      type="password"
                      {...register("password", { required: "Contraseña es requerida" })}
                      className={`w-full px-3 py-2 border ${errors.password ? 'border-red-500' : 'border-gray-300'} rounded-3xl`}
                  />
                  {errors.password && <p className="text-red-500 text-xs">{errors.password.message}</p>}
                </div>

                <button
                    type="submit"
                    className="w-full bg-primary hover:bg-opacity-90 text-black font-bold py-3 px-6 rounded-3xl"
                >
                  Iniciar sesión
                </button>

                <div className="text-center text-sm">
                  <span className="text-black">Olvidaste tu contraseña? </span>
                  <Link
                      to="/recover-password"
                      className="text-blue-600 hover:underline"
                  >
                    Recuperar
                  </Link>
                </div>
              </form>
            </div>
          </div>
        </div>
      </div>
  );
};

export default Login;
