import React from 'react';
import Header from '../../components/common/Header/Header';

const HomePage: React.FC = () => {
  return (
    <div className="min-h-screen flex flex-col">
      <Header />

      {/* Hero Section */}
      <div className="relative flex-grow">
        {/* Background Image with Overlay */}
        <div className="absolute inset-0">
          <img 
            src={'../../assets/images/home.png'} 
            alt="Logistics Services" 
            className="w-full h-full object-cover"
          />
          <div className="absolute inset-0 bg-black bg-opacity-60"></div>
        </div>

        {/* Content */}
        <div className="relative z-10 container mx-auto px-4 py-32 flex flex-col justify-center h-full">
          <h1 className="text-white text-4xl md:text-5xl lg:text-6xl font-bold max-w-3xl mb-6">
            Soluciones logísticas para tu negocio
          </h1>
          <p className="text-white text-lg md:text-xl max-w-2xl mb-10">
            Conectamos transportistas con empresas para optimizar tus envíos de manera eficiente y segura.
          </p>
          <div>
            <button className="bg-primary hover:bg-opacity-90 text-black font-bold py-3 px-8 rounded-md shadow-lg text-lg">
              Comenzar ahora
            </button>
          </div>
        </div>
      </div>
    </div>
  );
};

export default HomePage;
